import { useState } from "react";
import Home from "../../Product List/Home/Home";
import ProductCard from "../../Product List/ProductCard/ProductCard";
import App from "../../../App";

const Cart = () => {
  const [cart, setCart] = useState([])

  const addToCart = (product) => {
    const newProduct = cart.find((item) => item.id === product.id);
    console.log(newProduct);

    if (newProduct === undefined) {
      setCart([...cart, { ...product, amount: 1 }]);
    } else {

      const newCart = cart.map((item) => {
        if (item.id === product.id) {
          return { ...newProduct, amount: newProduct.amount + 1 };
        } else {
          return item;
        }
      });
      setCart(newCart)
    }
  };

  const deleteProductCart = (product) => {
    const ProductToDelete = cart.find((item) => item.id === product.id )


    if (ProductToDelete.amount>1) {

      const newCart = cart.map((item) => {
        if (item.id === product.id) {
          return { ... ProductToDelete, amount: ProductToDelete.amount-1 };
        } else {
          return item;
        }
      });
      setCart(newCart)

    } else {
      const newCart = cart.filter((item)=>{
        return item.id !== product.id
      })
      setCart(newCart)
    }
  }


return (
  <>
  <App addToCart={addToCart} 
  deleteProductCart={deleteProductCart} />
</>

)
}

export default Cart;
