import { CartContainer } from "./cartStyle"
import Items from "../Items/Items"
import { useEffect } from "react";
import ProductCard from "../../Product List/ProductCard/ProductCard";


const Cart = (props) => {

    const { ProductList, quantia, carrinho, amount, setAmount, cart, setCart } = props 

    useEffect(() => {
        const cartData = localStorage.getItem("cart");
        if (cartData) {
          setCart(JSON.parse(cartData));
        }
      }, []);

      const addItemToCart = (ProductList) => {
        setCart([...cart, ProductList]);
        localStorage.setItem("cart", JSON.stringify(cart));

        return (
            <ProductCard
            addItemToCart={addItemToCart}
            product={product}/>
        )
        };


    /*return( 
